import React, { useEffect, useState } from 'react';
import { useParams } from "react-router-dom";
import axios from "axios";

function Post() {
  let { id } = useParams();
  const [post, setPost] = useState({}); // Estado para armazenar o post

  useEffect(() => {
    axios.get(`http://localhost:3001/posts/byId/${id}`).then((response) => {
      setPost(response.data); // Atualiza o estado com os dados do post
    });
  }, []); // Executa o efeito quando o `id` mudar

  return (
    <div>
      <div>Post ID: {id}</div>
      <h1>{post.title}</h1> {/* Agora `post.title` ser√° renderizado corretamente */}
    </div>
  );
}

export default Post;
